import React from "react";

import { Table, Button } from "react-bootstrap";

import Modal from "../../components/Modal";

import User from "../../api/User";

import Form from "./Form";

export default class Users extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      users: [],
      activeModal: false,
      form: {
        name: "",
      },
    };

    this.closeModal = this.closeModal.bind(this);
    this.openModal = this.openModal.bind(this);

    this.users = this.users.bind(this);

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  users() {
    User.get().then(({ data }) => {
      this.setState({
        users: data,
      });
    });
  }

  closeModal() {
    this.setState({
      activeModal: false,
    });
  }

  openModal() {
    this.setState({
      activeModal: true,
    });
  }

  handleChange(event) {
    const { name, value } = event.target;
    let params = {};
    params[name] = value;

    this.setState({ form: { ...this.state.form, ...params } });
  }

  handleSubmit(event) {
    event.preventDefault();

    console.log(this.state.form)
  }

  componentDidMount() {
    this.users();
  }

  content() {
    const { users } = this.state;

    if (!users.length) {
      return (
        <tr>
          <td colSpan="2">No user found</td>
        </tr>
      );
    }

    return users.map((user) => (
      <tr key={user._id}>
        <td>{user._id}</td>
        <td>{user.name}</td>
      </tr>
    ));
  }

  render() {
    return (
      <>
        <div className="d-flex justify-content-between mb-1">
          <span>Users</span>
          <Button onClick={this.openModal} size="sm" variant="primary">
            Adicionar
          </Button>
        </div>
        <Table striped bordered hover responsive size="sm">
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
            </tr>
          </thead>

          <tbody>{this.content()}</tbody>
        </Table>
        <Modal
          activeModal={this.state.activeModal}
          closeModal={this.closeModal}
          submitButton={
            <Button variant="primary" onClick={this.handleSubmit}>
              Save Changes
            </Button>
          }
        >
          {<Form form={this.state.form} handleChange={this.handleChange} />}
        </Modal>
      </>
    );
  }
}
