import React from "react";

import { Table, Button, Badge } from "react-bootstrap";

import UserComponent from "./User";

import Modal from "../../components/Modal";

import User from "../../api/User";

import Form from "./Form";

const defaultForm = {
  name: "",
  _id: null,
};

export default class Users extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      users: [],
      user: null,
      selected: [],
      activeModal: false,
      form: defaultForm,
      mode: "create",
    };

    this.closeModal = this.closeModal.bind(this);
    this.openModal = this.openModal.bind(this);

    this.toggleSelected = this.toggleSelected.bind(this);

    this.user = this.show.bind(this);
    this.users = this.users.bind(this);
    this.store = this.store.bind(this);
    this.update = this.update.bind(this);
    this.delete = this.delete.bind(this);

    this.clear = this.clear.bind(this);
    this.fill = this.fill.bind(this);
    this.add = this.add.bind(this);
    this.edit = this.edit.bind(this);

    this.changeMode = this.changeMode.bind(this);

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async users() {
    await User.get().then(({ data }) => {
      this.setState({
        users: data,
      });
    });
  }

  async store() {
    await User.store(this.state.form)
      .then(({ data }) => {
        this.setState({
          users: [...this.state.users, data],
        });
        this.closeModal();
      })
      .catch((err) => {
        console.log(err.response);
      });
  }

  async update() {
    await User.update(this.state.form)
      .then(() => {
        this.users();
        this.closeModal();
      })
      .catch((err) => {
        console.log(err.response);
      });
  }

  async delete({ _id }) {
    await User.destroy(_id)
      .then(() => {
        this.users();
      })
      .catch((err) => {
        console.log(err.response);
      });
  }

  isSelected(model) {
    return this.state.selected.filter((item) => {
      return item._id === model._id;
    });
  }

  toggleSelected(model) {

    if (this.isSelected) {
      let selected = this.state.selected.filter((item) => {
        return item._id !== model._id;
      });

      this.setState({
        selected: selected,
      });
    } else {
      console.log("adding", model);
      this.setState({
        selected: [...this.state.selected, model],
      });
    }
  }

  show(user) {
    this.changeMode("view");
    this.setState({ user: user });
    this.openModal();
  }

  changeMode(mode) {
    this.setState({
      mode: mode,
    });
  }

  add() {
    this.changeMode("create");
    this.openModal();
  }

  edit(model) {
    this.changeMode("edit");

    this.fill(model);
    this.openModal();
  }

  closeModal() {
    this.setState({
      activeModal: false,
    });

    this.clear();
  }

  openModal() {
    this.setState({
      activeModal: true,
    });
  }

  fill(model) {
    this.setState({ form: model });
  }

  clear() {
    this.setState({ form: defaultForm });
  }

  handleChange(event) {
    const { name, value } = event.target;
    let params = {};
    params[name] = value;

    this.setState({ form: { ...this.state.form, ...params } });
  }

  handleSubmit(event) {
    event.preventDefault();
    if (this.state.mode === "create") {
      this.store();
    } else if (this.state.mode === "edit") {
      this.update();
    } else {
      return null;
    }
  }

  componentDidMount() {
    this.users();
  }

  content() {
    const { users } = this.state;

    if (!users.length) {
      return (
        <tr>
          <td colSpan="2">No user found</td>
        </tr>
      );
    }

    return users.map((user) => (
      <tr key={user._id}>
        <td>
          <span onClick={() => this.toggleSelected(user)}>{user._id}</span>{" "}
          <Badge onClick={() => this.edit(user)} variant="success">
            edit
          </Badge>
          <Badge
            className="ml-1"
            onClick={() => {
              this.user(user);
            }}
            variant="primary"
          >
            view
          </Badge>
          <Badge
            className="ml-1"
            onClick={() => this.delete(user)}
            variant="danger"
          >
            destroy
          </Badge>
        </td>
        <td>{user.name}</td>
      </tr>
    ));
  }

  render() {
    return (
      <>
        <div className="d-flex justify-content-between mb-1">
          <span>Users</span>
          <div>
            <Button onClick={this.add} size="sm" variant="primary">
              Add
            </Button>
            {this.state.selected.length ? (
              <Button onClick={this.add} size="sm" variant="primary">
                Remove selected {this.state.selected.length}
              </Button>
            ) : (
              ""
            )}
          </div>
        </div>
        <Table striped bordered hover responsive size="sm">
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
            </tr>
          </thead>

          <tbody>{this.content()}</tbody>
        </Table>
        <Modal
          activeModal={this.state.activeModal}
          closeModal={this.closeModal}
          showSubmit={this.mode !== "view"}
          submitButton={
            <Button variant="primary" onClick={this.handleSubmit}>
              Save Changes
            </Button>
          }
        >
          {this.state.mode === "view" ? (
            <UserComponent user={this.state.user} />
          ) : (
            <Form form={this.state.form} handleChange={this.handleChange} />
          )}
        </Modal>
      </>
    );
  }
}
